";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"1.07061";s:4:"name";s:7:"Levitra";}i:3784;a:5:{s:10:"id_packing";s:4:"3784";s:5:"count";s:3:"270";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"1.02395";s:4:"name";s:7:"Levitra";}i:3785;a:5:{s:10:"id_packing";s:4:"3785";s:5:"count";s:3:"360";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"1.00062";s:4:"name";s:7:"Levitra";}}}}