<li><span id="result_box" lang="fr"><span class="hps">avez les probl&egrave;mes h&eacute;patiques</span><span>, en particulier</span> <span class="hps">la cirrhose du foie</span><span>.</span></span></li>
<li><span id="result_box" lang="fr"><span class="hps">avez des probl&egrave;mes r&eacute;naux</span><span>.</span></span></li>
<li><span id="result_box" lang="fr"><span class="hps alt-edited">avez un</span> <span class="hps">trouble de l'alimentation</span> <span class="hps alt-edited">tel que</span> <span class="hps">l'anorexie ou</span> <span class="hps">la boulimie</span><span>.</span></span></li>
<li><span id="result_box" lang="fr"><span class="hps alt-edited">avez</span> <span class="hps">eu un traumatisme cr&acirc;nien</span><span>.</span><span class="hps alt-edited">&nbsp;</span></span></li>
<li><span id="result_box" lang="fr"><span class="hps alt-edited">avez eu une</span> <span class="hps">saisie (la convulsion</span><span>,</span> <span class="hps alt-edited">la crampe).</span><span class="hps alt-edited">&nbsp;</span></span></li>
<li><span id="result_box" lang="fr"><span class="hps alt-edited">avez une tumeur dans</span> <span class="hps">votre syst&egrave;me nerveux</span> <span class="hps alt-edited"> (dans le cerveau</span> <span class="hps alt-edited">ou la moelle</span><span>)</span><span>.</span></span></li>
