2:"60";s:6:"dosage";d:200;s:14:"price_per_pill";s:7:"0.84641";s:4:"name";s:8:"Etodolac";}i:3465;a:5:{s:10:"id_packing";s:4:"3465";s:5:"count";s:2:"90";s:6:"dosage";d:200;s:14:"price_per_pill";s:7:"0.76768";s:4:"name";s:8:"Etodolac";}i:3466;a:5:{s:10:"id_packing";s:4:"3466";s:5:"count";s:3:"120";s:6:"dosage";d:200;s:14:"price_per_pill";s:7:"0.72831";s:4:"name";s:8:"Etodolac";}i:3467;a:5:{s:10:"id_packing";s:4:"3467";s:5:"count";s:3:"180";s:6:"dosage";d:200;s:14:"price_per_pill";s:7:"0.68894";s:4:"name";s:8:"Etodolac";}i:3468;a:5:{s:10:"id_packing";s:4:"3468";s:5:"count";s:3:"270";s:6:"dosage";d:200;s:14:"price_per_pill";s:7:"0.66269";s:4:"name";s:8:"Etodolac";}i:3469;a:5:{s:10:"id_packing";s:4:"3469";s:5:"count";s:3:"360";s:6:"dosage";d:200;s:14:"price_per_pill";s:7:"0.64957";s:4:"name";s:8:"Etodolac";}}}}