10;a:6:{i:2622;a:5:{s:10:"id_packing";s:4:"2622";s:5:"count";s:2:"60";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"0.90706";s:4:"name";s:8:"Nolvadex";}i:2623;a:5:{s:10:"id_packing";s:4:"2623";s:5:"count";s:2:"90";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"0.75105";s:4:"name";s:8:"Nolvadex";}i:2624;a:5:{s:10:"id_packing";s:4:"2624";s:5:"count";s:3:"120";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"0.67304";s:4:"name";s:8:"Nolvadex";}i:2625;a:5:{s:10:"id_packing";s:4:"2625";s:5:"count";s:3:"180";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"0.59503";s:4:"name";s:8:"Nolvadex";}i:2626;a:5:{s:10:"id_packing";s:4:"2626";s:5:"count";s:3:"270";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"0.54303";s:4:"name";s:8:"Nolvadex";}i:2627;a:5:{s:10:"id_packing";s:4:"2627";s:5:"count";s:3:"360";s:6:"dosage";d:10;s:14:"price_per_pill";s:7:"0.51702";s:4:"name";s:8:"Nolvadex";}}}}